var max = (msg.payload.result.bandwidth_down/1000).toPrecision(3);
var data = (msg.payload.result.rate_down*8/1000).toPrecision(3);
units = "kbps"
if(data>999){
    data/=1000;
    max/=1000;
    units = "Mbps"
}
msg = {
    payload:data,
    ui_control:{min:0,max:max},
    units:units
};
return msg;

var max = (msg.payload.result.bandwidth_up/1000).toPrecision(3);
var data = (msg.payload.result.rate_up*8/1000).toPrecision(3);
units = "kbps"
if(data>999){
    data/=1000;
    max/=1000;
    units = "Mbps"
}
msg = {
    payload:data,
    ui_control:{min:0,max:max},
    units:units
};
return msg;

switch (msg.payload.result.state) {

case "going_up" : 
    msg.color = "yellow";
    msg.payload = "initialisation ...";
    break;
case "up" : 
    msg.color = "lime";
    msg.payload = "active";
    break;
case "going_down" : 
    msg.color = "orange";
    msg.payload = "desactivation ...";
    break;
case "down" : 
    msg.color = "red";
    msg.payload = "désactivé";
    break;
default : 
    msg.color = "red";
    msg.payload = msg.payload.result.state;
    break;
}

return msg;

